const express = require('express');
const axios = require('axios');
const app = express();

const clients = {
  'client123': { secret: 'sec456', redirectUris: ['https://legit.com/callback'] }
};

app.get('/oauth/authorize', (req, res) => {
  const { client_id, redirect_uri, state } = req.query;
  const client = clients[client_id];

  if (!client) return res.status(400).send('Invalid client');

  if (req.query.response_type === 'code') {
    const authCode = 'auth_' + Math.random().toString(36).substr(2, 9);
    const redirectUrl = new URL(redirect_uri);
    redirectUrl.searchParams.set('code', authCode);
    if (state) redirectUrl.searchParams.set('state', state);
    res.redirect(redirectUrl.toString());
  } else {
    res.status(400).send('Unsupported response_type');
  }
});

app.post('/oauth/token', (req, res) => {
  const { client_id, client_secret, code } = req.body;
  const client = clients[client_id];

  if (!client || client.secret !== client_secret) {
    return res.status(401).json({ error: 'invalid_client' });
  }

  res.json({
    access_token: 'token_' + Math.random().toString(36).substr(2, 16),
    token_type: 'bearer',
    expires_in: 3600
  });
});

app.listen(3000);

