const express = require('express');
const jwt = require('jsonwebtoken');
const fs = require('fs');
const app = express();

const users = {
  'user': { password: 'normalpass', role: 'user' },
  'admin': { password: 'adminpass', role: 'admin' }
};

app.post('/login', (req, res) => {
  const { username, password } = req.body;
  if (users[username] && users[username].password === password) {
    const token = jwt.sign({ user: username, role: users[username].role }, 'secretKey', { header: { kid: 'safeKey' } });
    res.json({ token });
  } else {
    res.status(401).send('Invalid credentials');
  }
});

app.get('/admin', (req, res) => {
  const token = req.headers.authorization?.split(' ')[1];
  try {
    const decoded = jwt.verify(token, (header, callback) => {
      const keyPath = `/var/www/keys/${header.kid}`;
      fs.readFile(keyPath, (err, key) => {
        callback(err, key);
      });
    });
    if (decoded.role === 'admin') {
      res.send('Admin panel accessed');
    } else {
      res.status(403).send('Forbidden');
    }
  } catch (err) {
    res.status(401).send('Invalid token');
  }
});

app.listen(3000);

