const express = require('express');
const { graphqlHTTP } = require('express-graphql');
const { buildSchema } = require('graphql');
const cookieParser = require('cookie-parser');

const app = express();
app.use(cookieParser());

const schema = buildSchema(`
  type Query {
    getUser: User
    getSession: Session
  }
  type Mutation {
    login(username: String!, password: String!): User
    createSession(username: String!): Session
    rotateSession: Session
  }
  type User {
    username: String
    role: String
  }
  type Session {
    id: String
    expires: String
  }
`);

const root = {
  getUser: (args, req) => {
    if (!req.cookies.session) throw new Error('Not authenticated');
    return { username: req.cookies.session.user, role: req.cookies.session.role };
  },
  getSession: (args, req) => {
    return { id: req.cookies.session?.id, expires: '2023-12-31' };
  },
  login: ({ username, password }, req) => {
    if (username === 'admin' && password === 'admin123') {
      if (!req.cookies.session) {
        req.cookies.session = { id: Math.random().toString(36).substr(2, 10) };
      }
      req.cookies.session.user = username;
      req.cookies.session.role = 'admin';
      return { username, role: 'admin' };
    }
    throw new Error('Invalid credentials');
  },
  createSession: ({ username }, req) => {
    const sessionId = Math.random().toString(36).substr(2, 10);
    return { id: sessionId, expires: '2023-12-31' };
  },
  rotateSession: (args, req) => {
    if (!req.cookies.session) throw new Error('No session');
    req.cookies.session.id = Math.random().toString(36).substr(2, 10);
    return { id: req.cookies.session.id, expires: '2023-12-31' };
  }
};

app.use('/graphql', graphqlHTTP({
  schema: schema,
  rootValue: root,
  graphiql: true
}));

app.listen(3000);

const express = require('express');
const jwt = require('jsonwebtoken');
const app = express();

const internalServices = {
  'user-service': 'http://internal-user-service'
};

app.use((req, res, next) => {
  const token = req.headers.authorization?.split(' ')[1];
  if (token) {
    try {
      const decoded = jwt.decode(token, { complete: true });
      if (req.headers['x-internal-request'] === 'true') {
        req.user = decoded.payload;
        return next();
      }
      jwt.verify(token, 'secretKey');
      req.user = decoded.payload;
    } catch (err) {
      return res.status(401).send('Invalid token');
    }
  }
  next();
});

app.get('/internal/user', async (req, res) => {
  const response = await fetch(`${internalServices['user-service']}/user`, {
    headers: {
      'Authorization': `Bearer ${req.headers.authorization?.split(' ')[1]}`,
      'X-Internal-Request': 'true'
    }
  });
  res.json(await response.json());
});

app.listen(3000);

