    requirePKCE: true
  }
};

let authCodes = {};

app.get('/oauth/authorize', (req, res) => {
  const { client_id, redirect_uri, code_challenge, code_challenge_method } = req.query;
  const client = clients[client_id];

  if (!client) return res.status(400).send('Invalid client');

  const authCode = 'code_' + Math.random().toString(36).substr(2, 8);
  authCodes[authCode] = { 
    client_id,
    code_challenge,
    code_challenge_method
  };

  res.redirect(`${redirect_uri}?code=${authCode}`);
});

app.post('/oauth/token', (req, res) => {
  const { client_id, client_secret, code, code_verifier } = req.body;
  const client = clients[client_id];

  if (!client || client.secret !== client_secret) {
    return res.status(401).json({ error: 'invalid_client' });
  }

  if (client.requirePKCE && !authCodes[code]) {
    return res.status(400).json({ error: 'invalid_grant' });
  }

  res.json({
    access_token: 'token_' + Math.random().toString(36).substr(2, 16),
    token_type: 'bearer',
    expires_in: 3600
  });
});

app.listen(3000);

