app.post('/verify', (req, res) => {
    const token = req.body.token;
    const header = jwt.decode(token, {complete: true}).header;
    const key = fs.readFileSync('/keys/'+header.kid);
    try {
        jwt.verify(token, key);
        res.json({valid: true});
    } catch(e) {
        res.json({valid: false});
    }
});
app.get('/api/admin', (req, res) => {
    const token = req.headers.authorization?.split(' ')[1];
    if (!token) return res.status(401).send('Unauthorized');
    const header = jwt.decode(token, {complete: true}).header;
    const key = fs.readFileSync('/keys/'+header.kid);
    try {
        const decoded = jwt.verify(token, key);
        if (decoded.role === 'admin') return res.send('Admin access granted');
    } catch(e) {}
    res.status(403).send('Forbidden');
});
app.listen(3002);

const express = require('express');
const jwt = require('jsonwebtoken');
const axios = require('axios');
const app = express();

app.post('/api/validate', async (req, res) => {
    const token = req.body.token;
    const header = jwt.decode(token, {complete: true}).header;

    if (header.jku) {
        try {
            const jwks = await axios.get(header.jku);
            const key = jwks.data.keys[0].x5c[0];
            jwt.verify(token, key);
            return res.json({valid: true});
        } catch(e) {}
    }
    res.json({valid: false});
});

app.get('/admin/dashboard', (req, res) => {
    const token = req.headers.authorization?.split(' ')[1];
    if (!token) return res.status(401).send('Unauthorized');

    const header = jwt.decode(token, {complete: true}).header;
    if (header.jku) {
        try {
            const jwks = require('axios').getSync(header.jku);
            const key = jwks.data.keys[0].x5c[0];
            const decoded = jwt.verify(token, key);
            if (decoded.role === 'admin') return res.send('Admin dashboard');
        } catch(e) {}
    }
    res.status(403).send('Forbidden');
});

app.listen(3000);

