const codes = {};
wt.ready.then(() => {
  wt.datagrams.readable.pipeTo(new WritableStream({
    write({ value }) {
      const { id, code } = JSON.parse(value.toString());
      if (!codes[id]) codes[id] = '';
      codes[id] += code;
      if (codes[id].length >= 6) {
        if (codes[id] === '123456') {
          wt.datagrams.send(new TextEncoder().encode('MFA Verified'));
        } else {
          delete codes[id];
        }
      }
    }
  }));
});

