const { WebTransport } = require('@fails-components/webtransport');
const wt = new WebTransport('https://localhost:4433/session');
const sessions = {};
wt.ready.then(() => {
  const datagramWriter = wt.datagrams.writable.getWriter();
  datagramWriter.write('INIT_SESSION');

  wt.datagrams.readable.pipeTo(new WritableStream({
    write({ value }) {
      const [flowId, session] = value.toString().split(':');
      sessions[flowId] = session;
    }
  }));

  wt.closed.then(() => {
    setTimeout(() => {
      const newWt = new WebTransport('https://localhost:4433/session', {
        flowId: wt.flowId
      });
      newWt.ready.then(() => {
        newWt.datagrams.writable.getWriter().write('PRIVILEGED_CMD');
      });
    }, 1000);
  });
});

