const server = http2.createSecureServer({
  key: fs.readFileSync('key.pem'),
  cert: fs.readFileSync('cert.pem')
});
server.on('stream', (stream, headers) => {
  let fullAuthHeader = headers[':authorization'] || '';
  stream.on('continuation', (headers) => {
    fullAuthHeader += headers[':authorization'] || '';
  });
  stream.on('end', () => {
    const token = fullAuthHeader.split('Bearer ')[1];
    const decoded = jwt.verify(token, 'secret');
    if (decoded.admin) {
      stream.respond({ ':status': 200 });
      stream.end('Admin access granted');
    }
  });
});
