const express = require('express');
const session = require('express-session');
const app = express();
app.use(session({ secret: 'secret123', resave: false, saveUninitialized: true }));
const users = [{ username: 'carlos', password: 'pass123' }];
app.post('/login', (req, res) => {
  const { username, password } = req.body;
  const user = users.find(u => u.username === username && u.password === password);
  if (user) {
    req.session.user = username;
    res.send('Login successful');
  } else {
    res.status(401).send('Invalid credentials');
  }
});
app.get('/my-account', (req, res) => {
  if (req.session.user) return res.send(`Welcome ${req.session.user}`);
  res.status(403).send('Not logged in');
});
app.listen(4001, () => console.log('Session Fixation Lab running on 4001'));

const express = require('express');
const app = express();
app.get('/auth', (req, res) => {
  const { redirect_uri } = req.query;
  if (redirect_uri.startsWith('https://trusted.com')) {
    const token = 'access-token-for-user';
    return res.redirect(`${redirect_uri}#access_token=${token}`);
  }
  res.status(400).send('Invalid redirect');
});
app.listen(5000, () => console.log('OAuth Redirect Lab running on port 5000'));
