const ldap = require('ldapjs');
const db = require('./db');

const server = ldap.createServer();

server.search('ou=people', (req, res) => {
  const filter = req.filter;

  // Vulnerable LDAP to SQL conversion
  const where = convertLdapToSQL(filter);
  const query = `SELECT * FROM employees WHERE ${where}`;

  db.query(query, (err, results) => {
    results.forEach(entry => {
      res.send(entryToLdap(entry));
    });
    res.end();
  });
});

function convertLdapToSQL(filter) {
  // Naive conversion that doesn't handle escaping
  return filter.toString()
    .replace(/\(([^)]+)\)/g, "$1")
    .replace(/\|/g, " OR ")
    .replace(/\&/g, " AND ");
}

