const express = require('express');
const jwt = require('jsonwebtoken');
const app = express();
app.get('/api/transfers', (req, res) => {
    const token = req.headers.authorization.split(' ')[1];
    const decoded = jwt.decode(token, { complete: true });
    if (decoded.header.alg === 'none') {
        const payload = decoded.payload;
        if (payload.role === 'admin') {
            return res.send('Admin transfer initiated');
        }
    }
    res.status(403).send('Forbidden');
});
app.listen(3000);

const express = require('express');
const cookieParser = require('cookie-parser');
const app = express();
app.use(cookieParser());
app.post('/login', (req, res) => {
    if (!req.cookies.sessionid) {
        res.cookie('sessionid', req.query.sid || Math.random().toString(36));
    }
    if (req.body.user === 'admin' && req.body.pass === 'S3cr3t!') {
        res.send('Logged in');
    } else {
        res.send('Login failed');
    }
});
app.listen(3000);

const express = require('express');
const app = express();
const codes = {};
app.get('/oauth/authorize', (req, res) => {
    const code = Math.random().toString(36).substring(2);
    codes[code] = { user: req.query.user_id };
    res.redirect(`${req.query.redirect_uri}?code=${code}`);
});
app.post('/oauth/token', (req, res) => {
    if (codes[req.body.code]) {
        res.json({ access_token: 'stolen_token' });
    } else {
        res.status(400).send('Invalid code');
    }
});
app.listen(3000);
