app.post('/price', (req, res) => {
  const { symbol, signature } = req.body;
  const query = `SELECT price FROM ${symbol}_prices
    WHERE block_number > 15438234`;

  if (!validateSig(signature, query)) return res.status(403);
  pool.query(query, (err, result) => {
    res.json({ price: median(result.rows) });
  });
});

