const WebSocket = require('ws');
const wss = new WebSocket.Server({ port: 8080 });

wss.on('connection', ws => {
  ws.on('message', message => {
    wss.clients.forEach(client => {
      if (client.readyState === WebSocket.OPEN) {
        client.send(`<p>${message}</p>`);
      }
    });
  });
});

app.post('/update-profile', (req, res) => {
  let { bio } = req.body;
  database.updateUserProfile(req.user.id, `<p>${bio}</p>`);
  res.redirect('/dashboard');
});

app.post('/upload', (req, res) => {
  let file = req.files.svg;
  file.mv('./uploads/' + file.name, () => {
    res.send('File uploaded!');
  });
});

app.post('/verify-token', (req, res) => {
  let decoded = jwt.verify(req.body.token, 'weaksecret');
  res.send(`<h1>Welcome ${decoded.username}</h1>`);
});

app.post('/update-bio', (req, res) => {
  let bio = req.body.bio;
  db.query(`UPDATE users SET bio='${bio}' WHERE id=${req.user.id}`);
  res.send('Bio updated');
});

app.get('/search', (req, res) => {
  let query = req.query.q;
  res.send(`<h2>Results for: ${query}</h2>`);
});

app.post('/submit-review', (req, res) => {
  let review = req.body.review;
  db.query(`INSERT INTO reviews (content) VALUES ('${review}')`);
  res.send('Review submitted');
});

app.get('/submit-feedback', (req, res) => {
  let feedback = req.query.feedback;
  res.send(`<p>Your feedback: ${feedback}</p>`);
});

app.get('/preview', (req, res) => {
  let img = req.query.img;
  res.send(`<img src='${img}'>`);
});
