const express = require('express');
const bodyParser = require('body-parser');
const app = express();

let feedbacks = [];
app.use(bodyParser.urlencoded({ extended: true }));

app.get('/feedback', (req, res) => {
  res.send(`
    <html>
      <body>
        <h2>Feedback Form</h2>
        <form action='/submit' method='POST'>
          <textarea name='msg' placeholder='Your feedback'></textarea>
          <button>Send</button>
        </form>
      </body>
    </html>
  `);
});

app.post('/submit', (req, res) => {
  feedbacks.push(req.body.msg);
  console.log('[SUBMIT] New feedback:', req.body.msg);
  res.redirect('/admin');
});

app.get('/admin', (req, res) => {
  const out = feedbacks.map(m => `<div>${m}</div>`).join('');
  res.send(`
    <html>
      <body>
        <h2>Admin Panel</h2>
        ${out}
      </body>
    </html>
  `);
});

app.listen(3000, () => console.log('Feedback system ready'));

