const express = require('express');
const bodyParser = require('body-parser');
const app = express();

let comments = [];
app.use(bodyParser.urlencoded({ extended: true }));

app.get('/blog', (req, res) => {
  const renderedComments = comments.map(c => `<p>${c}</p>`).join('');
  res.send(`
    <html>
      <body>
        <h2>Blog Post</h2>
        <p>Interesting article about security!</p>
        <h3>Comments</h3>
        ${renderedComments}
        <form action='/comment' method='POST'>
          <input name='text' placeholder='Comment here'>
          <button>Post</button>
        </form>
      </body>
    </html>
  `);
});

app.post('/comment', (req, res) => {
  console.log('[LOG] New comment:', req.body.text);
  comments.push(req.body.text);
  res.redirect('/blog');
});

app.listen(3000, () => console.log('Blog server running'));

