const express = require('express');
const { graphqlHTTP } = require('express-graphql');
const { buildSchema } = require('graphql');
const http = require('http');
const WebSocket = require('ws');

// Simple GraphQL schema with subscription simulation
const schema = buildSchema(`
  type Query { info: String }
  type Mutation { sendUpdate(message: String!): String }
`);

let currentMessage = '';
const root = {
  info: () => currentMessage,
  sendUpdate: ({ message }) => {
    // Vulnerable: storing unsanitized subscription message
    currentMessage = message;
    return 'Update sent';
  }
};

const app = express();
app.use('/graphql', graphqlHTTP({
  schema,
  rootValue: root,
  graphiql: true
}));

const server = http.createServer(app);
const wss = new WebSocket.Server({ server });

// Simulate pushing subscription updates to all clients
setInterval(() => {
  wss.clients.forEach(client => {
    if (client.readyState === WebSocket.OPEN) {
      client.send(currentMessage);
    }
  });
}, 5000);

server.listen(3000, () => console.log('GraphQL subscription server running on port 3000'));

