const express = require('express');
const bodyParser = require('body-parser');
const app = express();
let content = '';
app.use(bodyParser.urlencoded({ extended: true }));

app.post('/save-content', (req, res) => {
  content = req.body.html;
  res.redirect('/preview');
});

app.get('/preview', (req, res) => {
  res.send(`<!DOCTYPE html><html><body><div id='preview'>${content}</div><script>const observer = new MutationObserver(() => {}); observer.observe(document.getElementById('preview'), {childList: true});</script></body></html>`);
});

app.listen(3000);

