const express = require('express');
const bodyParser = require('body-parser');
const app = express();
const newsletters = {};
let id = 1;
app.use(bodyParser.urlencoded({ extended: true }));
app.post('/submit-newsletter', (req, res) => {
  newsletters[id] = req.body.content;
  res.redirect('/archive/' + id++);
});
app.get('/archive/:id', (req, res) => {
  res.send(`<html><body>${newsletters[req.params.id]}</body></html>`);
});
app.listen(3000);

const express = require('express');
