});
app.listen(3000);

const express = require('express');
const bodyParser = require('body-parser');
const app = express();
let faqs = [];
app.use(bodyParser.urlencoded({ extended: true }));
app.post('/admin/faq', (req, res) => {
  faqs.push({ q: req.body.question, a: req.body.answer });
  res.redirect('/faq');
});
app.get('/faq', (req, res) => {
  let html = '<html><body>';
  faqs.forEach(f => {
    html += `<div><h3>${f.q}</h3><p>${f.a}</p></div>`;
  });
  res.send(html + '</body></html>');
});
app.listen(3000);

