const express = require('express');
const app = express();
app.get('/generate-pdf', (req, res) => {
  const userInput = decodeURIComponent(req.query.transactionId);
  res.send(`<script>
    PDFObject.embed("/api/pdf?data=${userInput}");
  </script>`);
});

app.post('/api/devices', (req, res) => {
  db.collection('devices').updateOne(
    { userId: req.user.id },
    { $set: { name: req.body.name } },
    { upsert: true }
  );
  res.status(201).send('Device created');
});

function sanitize(input) {
  return input.replace(/<script/gi, '')
    .replace(/on\w+=/gi, 'data-')
    .replace(/javascript:/gi, '');
}
// Client-side rendering:
document.getElementById('chat').innerHTML = sanitize(userMessage);

app.use('/graphql', (err, req, res, next) => {
  res.status(400).json({
    error: `Invalid parameter: '${req.query.q}'`
  });
});

function markdownToHtml(text) {
  return text.replace(/\[([^\]]+)\]\(([^\)]+)\)/g, '<a href="$2">$1</a>');
}
// Rendered as:
document.getElementById('content').innerHTML = markdownToHtml(userInput);

new Vue({
  el: '#bio',
  template: `<div>${userBio}</div>`
});

const parser = new WebVTT.Parser();
parser.oncue = cue => {
  player.textTracks[0].addCue(cue);
};
parser.parse(userSubtitleData);

chrome.storage.local.get(['userPrefs'], data => {
  document.getElementById('config').innerHTML = data.userPrefs;
});

const svgPreview = `<svg width="${userWidth}" height="${userHeight}">
  <path d="${userDesign}"/>
</svg>`;
document.getElementById('preview').innerHTML = svgPreview;

app.get('/dicom-metadata', (req, res) => {
  const dicomData = parseDicom(req.query.file);
  res.send(`<table>${dicomData.tags.map(t => `<tr><td>${t.name}</td><td>${t.value}</td></tr>`).join('')}</table>`);
});

const wasmMemory = new WebAssembly.Memory({ initial: 256 });
const imports = { env: { memory: wasmMemory } };
WebAssembly.instantiateStreaming(fetch('chart.wasm'), imports);

const token = jwt.decode(localStorage.getItem('token'));
function UserInfo() {
  return <div dangerouslySetInnerHTML={{__html: token.user}} />;
}

pc.ondatachannel = e => {
  e.channel.onmessage = msg => {
    document.getElementById('chat').innerHTML += msg.data;
  };
};

app.setAsDefaultProtocolClient('electron');
app.on('open-url', (event, url) => {
  mainWindow.loadURL(url);
});

function renderPublicKey(key) {
  const coefficients = key.match(/.{1,4}/g);
  document.getElementById('visualizer').innerHTML = 
    `<svg>${coefficients.map(c => `<rect data-value="${c}"></rect>`).join('')}</svg>`;
}

async function fetchMetadata(cid) {
  const res = await ipfs.cat(cid);
  const metadata = JSON.parse(res);
  document.getElementById('traits').innerHTML = 
    metadata.attributes.map(a => `${a.trait_type}: ${a.value}`).join('<br>');
}

const loader = new THREE.GLTFLoader();
loader.load(modelUrl, gltf => {
  document.getElementById('model-info').innerHTML = 
    gltf.userData.comment;
});

func renderTooltip(labels map[string]string) string {
  return fmt.Sprintf(`<div class="tooltip">%s</div>`, 
    strings.Join(formatLabels(labels), "<br>"))
}

new Vue({
  el: '#hyperparams',
  data: { config: trainingConfig },
  template: `<div v-html="config.lr_scheduler"></div>`
});

app.get('/oracle-events', async (req, res) => {
  const events = await contract.queryFilter('AnswerUpdated');
  res.send(`<div>${events.map(e => e.args.description).join('')}</div>`);
});

map.on('click', e => {
  const props = e.features[0].properties;
  popup.setHTML(`<h3>${props.title}</h3><p>${props.description}</p>`);
});

mqttClient.on('message', (topic, message) => {
  const data = JSON.parse(message);
  document.getElementById(topic).innerHTML = 
    `<tr><td>${data.status}</td></tr>`;
});

app.post('/webauthn/register', (req, res) => {
  const attestation = cbor.decode(req.body.attestation);
  res.render('debug-view', { authenticator: attestation.authData });
});

app.get('/error/:function', async (req, res) => {
  const logs = await getLambdaLogs(req.params.function);
  res.send(`<pre>${logs.join('
').replace(/\n/g, '<br>')}</pre>`);
});
