export default async function handler(req, res) {
  await cosmos.items.create({ message: req.body.text });
  res.status(200).json({ success: true });
}

yjsConnector.on('message', msg => {
  document.getElementById('collab-users').innerHTML += 
    `<div>${msg.user} is editing</div>`;
});

function updateScore(scorePtr) {
  const score = UTF8ToString(scorePtr);
  document.getElementById('score').innerHTML = score;
}

navigator.bluetooth.requestDevice({filters: [{name: userSearchTerm}]})
.then(device => {
  document.getElementById('devices').innerHTML += `<li>${device.name}</li>`;
});

// superset_config.py
FEATURE_FLAGS = {
  "ENABLE_TEMPLATE_PROCESSING": True
}

const reader = port.readable.getReader();
while (true) {
  const { value } = await reader.read();
  document.getElementById('output').innerHTML += new TextDecoder().decode(value);
}

Matter.World.add(engine.world, [
  Matter.Bodies.rectangle(400, 610, 810, 60, { 
    render: {
      fillStyle: '#transparent',
      text: userData.label
    }
  })
]);

function renderHands(hands) {
  const svg = hands.map(h => 
    `<circle cx="${h.x}" cy="${h.y}" r="${h.radius}"/>`);
  document.getElementById('debug').innerHTML = svg.join('');
}

const state = prosemirrorStateFromJSON(schema, req.body.content);
document.getElementById('version-diff').innerHTML = 
  renderDiff(previousState, state);

navigator.nfc.watch(msg => {
  const text = new TextDecoder().decode(msg.records[0].data);
  exhibitInfoEl.innerHTML = text;
});

vscode.markdown.registerMarkdownPreviewProvider('md', {
  provideTextDocumentContent(uri) {
    return marked.parse(fs.readFileSync(uri.fsPath));
  }
});

app.get('/trace/:id', async (req, res) => {
  const trace = await tracer.get(req.params.id);
  res.render('trace-view', { spans: trace.spans });
});

const shader = gl.createShader(gl.FRAGMENT_SHADER);
gl.shaderSource(shader, userCode);
gl.compileShader(shader);
document.getElementById('shader-errors').innerHTML = gl.getShaderInfoLog(shader);

const reader = transport.datagrams.readable.getReader();
while (true) {
  const { value } = await reader.read();
  debugPanel.innerHTML += new TextDecoder().decode(value);
}

const decoder = new VideoDecoder({
  output: frame => {
    timelineSVG.innerHTML += `<text x="${frame.timestamp}">${frame.metadata.comment}</text>`;
  }
});

// islands/MyIsland.tsx
export default function MyIsland(props) {
  return <div dangerouslySetInnerHTML={{ __html: props.data }} />;
}

const adapter = await navigator.gpu.requestAdapter();
const device = await adapter.requestDevice();
const shaderModule = device.createShaderModule({ code: userCode });
document.getElementById('errors').innerHTML = shaderModule.compilationInfo.messages;

const builder = new MLGraphBuilder(context);
const desc = {name: userModel.metadata.author};
const output = builder.input('input', desc);
document.getElementById('model-info').innerHTML = output.name;

async function loadPost(cid) {
  const content = await ipfs.cat(cid);
  document.getElementById('post').innerHTML = content;
}

const webcontainer = await WebContainer.boot();
await webcontainer.mount({ '/proc': createProcMount() });
document.getElementById('env').innerHTML = await webcontainer.fs.readFile('/proc/self/environ');

const module = await WebAssembly.compile(userBuffer);
const excinfo = WebAssembly.Module.customSections(module, '.eh_frame');
document.getElementById('exceptions').innerHTML = 
  new TextDecoder().decode(excinfo[0]);
