// Naive slow compare function that introduces a fixed delay for each matching character
function slowCompare(a, b) {
    if (a.length !== b.length) {
        return false;
    }
    let result = true;
    for (let i = 0; i < a.length; i++) {
        // Introduce a 50ms delay for each character comparison
        const start = Date.now();
        while (Date.now() - start < 50) {}
        if (a[i] !== b[i]) {
            result = false;
        }
    }
    return result;
}

