const express = require('express');
const bodyParser = require('body-parser');
const speakeasy = require('speakeasy');
const app = express();
app.use(bodyParser.json());

const users = [{
  username: 'carlos',
  password: 'admin123',
  mfaSecret: speakeasy.generateSecret({ length: 20 }).base32,
  isAdmin: true
}];

const activeSessions = new Map();

app.post('/login', (req, res) => {
  const { username, password } = req.body;
  const user = users.find(u => u.username === username && u.password === password);

  if (!user) return res.status(401).send('Invalid credentials');

  // Return MFA requirement but expose vulnerability trigger
  res.status(202).json({
    message: 'MFA required',
    remainingAttempts: 'unlimited' // Critical flaw: No attempt counter
  });
});

app.post('/verify', (req, res) => {
  const { username, code } = req.body;
  const user = users.find(u => u.username === username);

  // VULNERABILITY: No rate-limiting + accepts old codes within 120s window
  const verified = speakeasy.totp.verify({
    secret: user.mfaSecret,
    encoding: 'base32',
    token: code,
    window: 4 // Allows codes from 2 minutes before/after
  });

  if (!verified) return res.status(401).send('Invalid MFA code');

  // Critical flaw: Doesn't invalidate used codes
  const sessionToken = Buffer.from(username + Date.now()).toString('base64');
  activeSessions.set(sessionToken, user);
  res.json({ token: sessionToken });
});

