const express = require('express');
const bodyParser = require('body-parser');
const app = express();
app.use(bodyParser.urlencoded({ extended: true }));
const users = { 'admin': '123' };
app.get('/login', (req, res) => {
  res.send('<form method="post"><input name="username"><input name="password" type="password"><button type="submit">Login</button></form>');
});
app.post('/login', (req, res) => {
  const { username, password } = req.body;
  if (users[username] && users[username] === password) {
    res.send(`Welcome, ${username}!`);
  } else {
    res.status(401).send('Invalid credentials');
  }
});
app.listen(3001, () => console.log('Server running on port 3001'));

const express = require('express');
const bodyParser = require('body-parser');
const session = require('express-session');
const app = express();
app.use(bodyParser.urlencoded({ extended: true }));
app.use(session({ secret: 'secret', resave: false, saveUninitialized: true }));
const users = { 'user1': 'password' };
app.get('/login', (req, res) => {
  res.send('<form method="post"><input name="username"><input name="password" type="password"><button type="submit">Login</button></form>');
});
app.post('/login', (req, res) => {
  const { username, password } = req.body;
  if (users[username] && users[username] === password) {
    req.session.username = username;
    res.send('Logged in');
  } else {
    res.status(401).send('Invalid credentials');
  }
});
app.get('/account', (req, res) => {
  if (req.session.username) {
    res.send(`Welcome, ${req.session.username}!`);
  } else {
    res.status(401).send('Please log in');
  }
});
app.get('/logout', (req, res) => {
  req.session.username = null; // Not destroying the session
  res.send('Logged out');
});
app.listen(3002, () => console.log('Server running on port 3002'));

